import React,{ useState} from 'react'
import Addperson from './components/Addperson'
import ShowNumbers from './components/ShowNumbers'

const App = () => {
    const [ persons, setPersons] = useState([
        { name: 'Arto Hellas', number: '040-123456' },
        { name: 'Ada Lovelace', number: '39-44-5323523' },
        { name: 'Dan Abramov', number: '12-43-234345' },
        { name: 'Mary Poppendieck', number: '39-23-6423122' }
      ]) 
    const [ newName, setNewName ] = useState('')
    const [newNumber,setNewNumber] = useState('')
    const [newFilter, setFilter] = useState('')

    const handleSubmitName = (event) =>{
        event.preventDefault()

        let nameIn=false
        for (let index = 0; index < persons.length; index++) {
            if (persons[index].name === newName){nameIn=true}
            
        }
        if (nameIn){window.alert(`${newName} is already added to phonebook`)}
        else setPersons(persons.concat({name : newName, number:newNumber}))

    }

    const handleOnChangeName = (event) =>{

        setNewName(event.target.value)
    }
    const handleOnChangeNumber = (event) =>{

        setNewNumber(event.target.value)
    }
    const handleFilterChange = (event) =>{

        setFilter(event.target.value)
    }

    return (
      <div>
        <h2>Phonebook</h2>
        <p>filter shown with </p><input value={newFilter} onChange={handleFilterChange}/>
                
        <Addperson newName={newName} newNumber={newNumber} handleOnChangeName={handleOnChangeName} handleOnChangeNumber={handleOnChangeNumber} handleSubmitName={handleSubmitName}/>
               

        {console.log('return')}
        <h2>Numbers</h2>
        <ShowNumbers persons={persons} newFilter={newFilter}/>
      </div>
    )
  
  }

export default App
