import React,{ useState,useEffect} from 'react'
import Addperson from './components/Addperson'
import ShowNumbers from './components/ShowNumbers'
import FilterNumbers from './components/FilterNumbers'
import axios from 'axios'
import PersonService from './services/PersonService'
import './index.css'

const App = () => {
    const [ persons, setPersons] = useState([]) 
    const [ newName, setNewName ] = useState('')
    const [newNumber,setNewNumber] = useState('')
    const [newFilter, setFilter] = useState('')
    const [errorMessage, setErrorMessage] = useState(null)

    useEffect(() => {
      console.log('effect')
      PersonService
        .getAll()
        .then(response => {
          setPersons(response.data)
        })
    }, [])

    const handleSubmitName = (event) =>{
        event.preventDefault()
        const newPerson ={name : newName, number:newNumber}
        let nameIn=false
        for (let index = 0; index < persons.length; index++) {
            if (persons[index].name === newName){nameIn=true}
            
        }
        if (nameIn){
         const p= persons.find(p=>p.name ===newName)
          if (p.number!==newNumber){
              const replaceConfirm=window.confirm("This person is already added to phonebook/n Would you like to replace the number?")
              if (replaceConfirm){
                PersonService.update(p.name,newNumber,p.id)}
                
            }
         else window.alert(`${newName} is already added to phonebook`)}
        else {
          //const newPerson ={name : newName, number:newNumber}
          PersonService
                      .create(newPerson)
                      .then(response=> setPersons(persons.concat(response.data)))
                      .catch(error=>{console.log("error in handleSubmitName")})
          
      }
    }

    const handleOnChangeName = (event) =>{

        setNewName(event.target.value)
    }
    const handleOnChangeNumber = (event) =>{

        setNewNumber(event.target.value)
    }
    const handleFilterChange = (event) =>{

        setFilter(event.target.value)
    }

    return (
      <div>
        <h1>Phonebook</h1>
        {errorMessage}
        <FilterNumbers handleFilterChange={handleFilterChange}/>
                
        <Addperson newName={newName} newNumber={newNumber} handleOnChangeName={handleOnChangeName} handleOnChangeNumber={handleOnChangeNumber} handleSubmitName={handleSubmitName} remove={(name,{persons})=>{PersonService.remove(name)}}/>
               

        {console.log('return')}
        <h2>Numbers</h2>
        <ShowNumbers persons={persons} newFilter={newFilter} remove={id=>{PersonService.remove(id)}} setPersons={(a)=>setPersons(a)}/>
      </div>
    )
  
  }

export default App
