import React from 'react'
import axios from 'axios'

const create = (newPerson)=>{

return axios.post('http://localhost:3001/persons',newPerson)

}

const getAll = ()=>{

return axios.get('http://localhost:3001/persons')

}

const remove = (name, persons)=>{
    nameObj = persons.find(person => person.name===name)
    return axios.delete(`http://localhost:3001/persons/${nameObj.id}`)

}


export default {create,getAll,remove}
